
let value1 = document.getElementById('value1')
let value2 = document.getElementById('value2')
let value3 = document.getElementById('value3')
let music=new Audio("start.wav")
let sound=new Audio("stop.wav")
let btn= document.getElementById('btn')
let stop= document.getElementById('stop')
let rld= document.getElementById('rld')
let values = [
    'ðŸ˜ƒ', 'ðŸ˜‡', 'ðŸ˜‹', 'ðŸ˜‚', 'ðŸ˜Ž', 'ðŸ˜­', 'ðŸ˜¡'
]
function getRandomValue() {
    return values[Math.floor(Math.random() * 7)]
}
let animationId;
let x=1
btn.onclick= ()=>{
    stop.disabled=false
    value1.style.animation="spinslot 0.2s linear infinite"
    value2.style.animation="slot 0.2s linear infinite"
    value3.style.animation="spinslot 0.2s linear infinite"
    music.play()
    music.loop=true
    btn.disabled=true
    if(btn.disabled==true){
        btn.innerHTML= '<s>START</s>'
    }
    if(stop.disabled==false){
        stop.innerHTML='STOP'
    }
    x=0
    animationId= setInterval(() => {

        value1.innerText = getRandomValue()
        value2.innerText = getRandomValue()
        value3.innerText = getRandomValue()
    
    }, 300)
}
function beep() {
    var snd = new Audio("data:audio/wav;base64,");  
    snd.play();
}
stop.onclick= ()=>{
    if(x!=1)
    {
        clearInterval(animationId)
        music.pause()
        btn.disabled=false
        if(btn.disabled==false){
            btn.innerHTML= 'START'
        }
        stop.disabled=true
        if(stop.disabled==true){
            stop.innerHTML='<s>STOP</s>'
        }
        x=1
        value1.style.animation=""
        beep()
        setTimeout(()=>{
            beep()
        value2.style.animation=""
        },300)
        setTimeout(()=>{
            beep()
        value3.style.animation=""
        },600)
        setTimeout(()=>{if((value1.innerText==value2.innerText)&&(value2.innerText==value3.innerText)){
            alertify.alert('SLOT MACHINE', 'Congratulations!YOU WON').set('autoReset', false); 
        }
        else{
            alertify.alert('SLOT MACHINE','Alas!YOU LOST').set('autoReset', false); 
        }    },900)
    }
  else
  {
    window.confirm('Game has not started')
  }
}
  rld.onclick= ()=>{
      location.reload()
  }
